import {mdbListGroup, mdbListGroupItem, mdbBadge} from 'mdbvue';
import {mdbContainer, mdbRow, mdbCol} from 'mdbvue';
import http from "../http-common";

export default {
    name: "PostsList",
    props: ['Post'],
    components: {
        mdbContainer,
        mdbRow,
        mdbCol,
        mdbListGroup,
        mdbListGroupItem,
        mdbBadge
    },
    data() {
        return {
            posts: []
        };
    },
    methods: {
        /* eslint-disable no-console */
        retrievePosts() {
            http.get("/posts")
                .then(response => {
                    this.posts = response.data;
                    console.log(response.data);
                })
                .catch(e => {
                    console.log(e);
                });
        },
        refreshList() {
            this.retrievePosts();
        },

    },
    mounted() {
        this.retrievePosts();
    }

}
